import React from 'react';
import { Link, Redirect } from "react-router-dom";
import { MDBContainer, MDBInputGroup, MDBBtn } from "mdbreact";
import { useSelector } from 'react-redux';


export default class Erfassung extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            created: false
        }
    }

    //save data and show success message
    handleCreate = (ev) => {
        ev.preventDefault();
        //set registrated = true will trigger a redirect to login page
        this.setState({
            created: true
        });

        //************************** */
        //api call here
        //************************** */

    }
    //reset the created state --> formular is visible again
    handleAgain = (ev) =>{
        this.setState({
            created: false
        });
    }

    /**
     * Render the component
     */

    render() {

        //success message
        if (this.state.created == true) {
            return (
                <div>
                <p>Der neue Eintrag ist bei uns eingegangen. Er wird nach einer Prüfung in die Datensammlung aufgenommen.</p>
                <button className="button" onClick={this.handleAgain}>Weiteren Flurnamen hinzufügen</button>
                </div>
            )
        } else {
            //render formular
            return (
                <main className="grid-container-x small callout content-background" role="Flurnamen Formular">
                    <h2>Datenerfassung</h2>

                    <MDBInputGroup
                        inputs={
                            <div className="custom-file">
                            <input
                                type="file"
                                className="custom-file-input"
                                id="inputGroupFile01"
                            />
                            <label className="custom-file-label" htmlFor="inputGroupFile01">
                                Bild hochladen
                            </label>
                            </div>
                        }
                        containerClassName="mb-3"
                        />

                    <form action="index.html" onSubmit={this.handleCreate}>
                        <div>
                            <label>Flurname
                    <input type="text" name="flur_name" placeholder="Flurname, den Sie hinzufügen möchten" tabIndex="1" required="required" value={this.state.flurName} onChange={this.updateFlurName} />
                            </label>
                        </div>
                        <div>
                            <label>Gemarkung (Ort)
                    <input type="text" name="gemarkung_name" placeholder="Geben Sie die Gemarkung(Ort) an, in der sich das Flurstück befindet" tabIndex="2" required="required" value={this.state.gemarkName} onChange={this.updateGemarkName} />
                            </label>
                        </div>
                        <div>
                            <label>Beleg des Names (optional)
                    <input type="text" name="beleg_verweis" placeholder="Geben Sie eine Referenz für den neuen Flurnamen an" tabIndex="3" value={this.state.evidence} onChange={this.updateEvidence} />
                            </label>
                        </div>
                        <div>
                            <label>Lage und Information
                    <textarea rows="4" name="information" placeholder="Geben Sie die Lage des Flurnames und weitere Informationen an" tabIndex="4" required="required" value={this.state.information} onChange={this.updateInformation} />
                            </label>
                        </div>
                        <div>
                            <label>Mundart (optional)
                    <input type="text" name="beleg_verweis" placeholder="Geben Sie eine mundartliche Aussprache an" tabIndex="5" value={this.state.dialect} onChange={this.updateDialect} />
                            </label>
                        </div>
                        <div>
                            <label>weitere Hinweise (optional)
                    <textarea rows="3" name="information" placeholder="Sie können noch weitere Hinweise, die nicht in die vorherigen Kategorien gepasst haben" tabIndex="6" value={this.state.note} onChange={this.updateNote} />
                            </label>
                        </div>
                        <div className="text-left">
                            <div className="grid-x grid-padding-x">
                                <div className="cell medium-3">
                                    <button tabIndex="7" className="button large expanded" name="add_button">Hinzufügen</button>
                                </div>
                            </div>
                        </div>
                    </form>
                </main>
            )
        }
    }
}
