import React from 'react';
import { Link, Redirect } from "react-router-dom";
import { MDBContainer, MDBInputGroup, MDBBtn, MDBInput } from "mdbreact";
import "bootstrap/dist/css/bootstrap.min.css";


export default class Erfassung extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            erstellt: false
        }
    }

    handleCreate = (event) => {
        event.preventDefault();

        this.setState({
            erstellt: true
        });

    }

    handleAgain = (ev) =>{
        this.setState({
            erstellt: false
        });
    }


    render() {
        //wenn der neue Eintrag hinzugefügt wurde
        if (this.state.erstellt == true) {

            return (
                <div><p>Der neue Eintrag ist bei uns eingegangen.Vielen Dank für den Beitrag!</p>
                <button className="button" onClick={this.handleAgain}>Weiteren Flurnamen hinzufügen</button>
                </div>
            )
        } else {

            return (
                <main className="grid-container-x small callout content-background" role="Flurnamen Formular">
                    <h2>Neuen Flurnamen hinzufügen</h2>

                <form onSubmit={this.handleCreate}>
                    <div className="form-group">
                        <div>
                            <label htmlFor="formGroupExampleInput">Flurname</label>
                            <input
                                type="text"
                                className="form-control"
                                id="formGroupExampleInput"
                                placeholder="Flurname, den Sie hinzufügen möchten"
                                required="required"
                            />
                        </div>
                        <div>
                            <label htmlFor="formGroupExampleInput">Gemarkung</label>
                            <input
                                type="text"
                                className="form-control"
                                id="formGroupExampleInput"
                                placeholder="Gemarkung (Ort) des Flurnames"
                            />
                        </div>
                        <div>
                            <label htmlFor="formGroupExampleInput">Beleg des Names</label>
                            <input

                                type="text"
                                className="form-control"
                                id="formGroupExampleInput"
                                placeholder=""
                            />
                        </div>
                        <div>
                            <label htmlFor="formGroupExampleInput">Mundart</label>
                            <input

                                type="text"
                                className="form-control"
                                id="formGroupExampleInput"
                                placeholder=""
                            />
                        </div>
                        <div>
                            <label htmlFor="formGroupExampleInput">Lage und Information</label>
                            <input

                                type="text"
                                className="form-control"
                                id="formGroupExampleInput"
                                placeholder=""
                            />
                        </div>
                        <div>

                            <MDBInput type="textarea" label="Weitere Hinweise?" background />
                        </div>
                    </div>


                        <MDBInputGroup
                                inputs={
                                    <div className="custom-file">
                                    <input
                                        type="file"
                                        className="custom-file-input"
                                        id="inputGroupFile01"
                                    />
                                    <label className="custom-file-label" htmlFor="inputGroupFile01">
                                        Datei hochladen
                                    </label>
                                    </div>
                                }
                                containerClassName="mb-3"
                                />

                            <div className="text-right">
                                <div className="grid-x grid-padding-x">
                                    <div className="cell medium-3">
                                        <button tabIndex="7" className="button large expanded" class="btn btn-success" name="add_button">Hinzufügen</button>

                                </div>
                            </div>
                        </div>
                    </form>
                    <button tabIndex="7" className="button large expanded" class="btn btn-danger" name="add_button">Abbrechen</button>
                </main>
            )
        }
    }
}
