import { render } from 'react-dom';
import React, { Component } from 'react';
import axios from 'axios';
import SearchAx from '../components/SearchAx';
import Suggestions from '../components/Suggestion';

import { MDBCol, MDBFormInline, MDBBtn, MDBIcon} from "mdbreact";

var finalIDs=[]

var getIDs = {
    method: 'get',
    url: 'https://collections.thulb.uni-jena.de/api/v1/search?q=+cbuUnitTypes.actual:(33.0%20%20%20%20%20%20%20%20%20%2033.1)&wt=json',
    headers: { 
      'Authorization': 'Basic cmVzdGFwaS1yZWFkOmRlMjgxMjMyOGIzYTZmODc1NDIyYjM4NzZlMDJiZTM3'
    }
  };

class Search extends Component {

  constructor(props) {
    super(props);
    this.state = {
        query: "",
        idList: [],
        results: [],
        filterBestand:[]
    };
  }

  async componentDidMount() {
    //first request
    await axios(getIDs).then((response)=>{
        const data = response.data.response.docs; //docs is a list
        for (var i = 0; i < data.length; i++)
        {
            var new_data={
                'id':data[i]['id'],
                'link':data[i]['link'][0],
                'ArchFile_class_001_Label':data[i]['ArchFile_class_001_Label'][1],
                'iviewFile':data[i]['iviewFile']
            }
            finalIDs.push(new_data)
        }


        this.setState({
            idList:finalIDs
        });
      })
      .catch((error)=>{
        console.log(error);
      });

    //others requests to get more information about objects
    var infoDetails=[]
    for(var i in [0]){
        var infos;

        var objID = finalIDs[i]['link'];
        var getInfo={
            method: 'get',
            url: 'https://collections.thulb.uni-jena.de/api/v1/objects/'+objID,
            headers: { 
            'Authorization': 'Basic cmVzdGFwaS1yZWFkOmRlMjgxMjMyOGIzYTZmODc1NDIyYjM4NzZlMDJiZTM3'
            }
        }
        await axios(getInfo).then((response)=>{

            var xml2js = require('react-native-xml2js');
            xml2js.parseString(response.data, (err, result) => {
              if(err) {
                  throw err;
              }
              const json = JSON.stringify(result);
              console.log(result);
              console.log(result['mycoreobject']);
              console.log(result.mycoreobject['metadata']);
              console.log(result.mycoreobject.structure);
              console.log(result.mycoreobject.service);

              infos = result.mycoreobject;
            });
         })
          .catch((error)=>{
            console.log(error);
          });
        infoDetails.push(infos);
    }
    this.setState({
      results:infoDetails
    });
  }

    handleInputChange = () => {
        this.setState({
        query: this.search.value,
        filterBestand:this.state.results.filter((bestand)=>bestand.metadata[0]["def.title"][0].title[0]["_"].toLowerCase().includes(this.state.query.toLowerCase()))
        })
    }
  render() {

    return (
      <div className="Search">

        <MDBCol>
            <div className="input-group md-form form-sm form-1 pl-0">
                <div className="input-group-prepend">
                <span className="input-group-text green lighten-3" id="basic-text1">
                    <MDBIcon className="text-white" icon="search" />
                </span>
                </div>

                    <input className="form-control my-0 py-1" 
                        ref={input => this.search = input}
                        onChange={this.handleInputChange}
                        type="text" 
                        placeholder="Flurnamen suchen..." 
                        aria-label="Search" />
            </div>
            {/*<p>{this.state.query}</p>*/}
        </MDBCol>

        {this.state.filterBestand.map((item, index) => <ResultList key={index} {...item} />)}
        <SearchAx/>
      </div>
    );
  }



}

const ResultList=(props)=>(

    <p><strong>Title: </strong> 
    {props.metadata[0]["def.title"][0].title[0]["_"]}
    </p>
);
export default Search;
