import React from 'react';
import { Link, Redirect } from "react-router-dom";
import { MDBContainer, MDBInputGroup, MDBBtn, MDBInput } from "mdbreact";
/**
 * Register Page
 */
export default class Erfassung extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            created: false
        }
    }
    //save data and show success message
    handleCreate = (ev) => {
        ev.preventDefault();
        //set registrated = true will trigger a redirect to login page
        this.setState({
            created: true
        });

    }
    //reset the created state --> formular is visible again
    handleAgain = (ev) =>{
        this.setState({
            created: false
        });
    }

    /**
     * Render the component
     */
    render() {
        //success message
        if (this.state.created == true) {
            return (
                <div>
                <p>Der neue Eintrag ist bei uns eingegangen.Vielen Dank für den Beitrag!</p>
                <button className="button" onClick={this.handleAgain}>Weiteren Flurnamen hinzufügen</button>
                </div>
            )
        } else {
            //render formular
            return (
                <main className="grid-container-x small callout content-background" role="Flurnamen Formular">
                    <h2>Neuen Flurnamen hinzufügen</h2>
                    <div className="form-group" onSubmit={this.handleCreate}>
                        <div>
                            <label htmlFor="formGroupExampleInput">Flurname</label>
                            <input
                                type="text"
                                className="form-control"
                                id="formGroupExampleInput"
                                placeholder="Flurname, den Sie hinzufügen möchten"
                                required="required"
                            />
                        </div>
                        <div>
                            <label htmlFor="formGroupExampleInput">Gemarkung</label>
                            <input
                                type="text"
                                className="form-control"
                                id="formGroupExampleInput"
                                placeholder="Gemarkung (Ort) des Flurnames"
                            />
                        </div>
                        <div>
                            <label htmlFor="formGroupExampleInput">Beleg des Names</label>
                            <input

                                type="text"
                                className="form-control"
                                id="formGroupExampleInput"
                                placeholder=""
                            />
                        </div>
                        <div>
                            <label htmlFor="formGroupExampleInput">Mundart</label>
                            <input

                                type="text"
                                className="form-control"
                                id="formGroupExampleInput"
                                placeholder=""
                            />
                        </div>
                        <div>
                            <label htmlFor="formGroupExampleInput">Lage und Information</label>
                            <input

                                type="text"
                                className="form-control"
                                id="formGroupExampleInput"
                                placeholder=""
                            />
                        </div>
                        <div>

                            <MDBInput type="textarea" label="Weitere Hinweise?" background />
                        </div>
                    </div>


                        <MDBInputGroup
                                inputs={
                                    <div className="custom-file">
                                    <input
                                        type="file"
                                        className="custom-file-input"
                                        id="inputGroupFile01"
                                    />
                                    <label className="custom-file-label" htmlFor="inputGroupFile01">
                                        Datei hochladen
                                    </label>
                                    </div>
                                }
                                containerClassName="mb-3"
                                />

                        <div className="text-left">
                            <div className="grid-x grid-padding-x">
                                <div className="cell medium-3">
                                    <button tabIndex="7" className="button large expanded" name="add_button">Hinzufügen</button>
                                </div>
                            </div>
                        </div>

                </main>
            )
        }
    }
}
