import { render } from 'react-dom';
import React, { Component } from 'react';
import axios from 'axios'


import { MDBCol, MDBFormInline, MDBBtn, MDBIcon} from "mdbreact";


var getIDs = {
    method: 'get',
    url: 'https://collections.thulb.uni-jena.de/api/v1/search?q=+cbuUnitTypes.actual:(33.0%20%20%20%20%20%20%20%20%20%2033.1)&wt=json',
    headers: { 
      'Authorization': 'Basic cmVzdGFwaS1yZWFkOmRlMjgxMjMyOGIzYTZmODc1NDIyYjM4NzZlMDJiZTM3'
    }
  };


class Search extends Component {

  constructor(props) {
    super(props);
    this.state = {
        query: "",
        results: []
    };
  }

  componentDidMount() {
    axios(getIDs).then((response)=>{
        console.log(response);

        const data = response.data.response.docs; //docs is a list

        var i;
        var id_results=[];
        var link_results=[];
        var label_results=[];
        var view_results=[];
        for (i = 0; i < data.length; i++) {
            id_results.push(data[i]['id']);
            link_results.push(data[i]['link'][0]);
            label_results.push(data[i]["ArchFile_class_001_Label"][1]);
            view_results.push(data[i]["iviewFile"]);
        }

        var final=[]
        for (i = 0; i < data.length; i++)
        {
            var new_data={
                'id':data[i]['id'],
                'link':data[i]['link'][0],
                'ArchFile_class_001_Label':data[i]['ArchFile_class_001_Label'][1],
                'iviewFile':data[i]['iviewFile']
            }
            final.push(new_data)
        }


        this.setState({
            results:final
        });
      })
      .catch((error)=>{
        console.log(error);
      });
  }
/*
    state = {
        query: '',
    }
 */   
    handleInputChange = () => {
        this.setState({
        query: this.search.value
        })
    }

  render() {

    return (
      <div className="Search">

        <MDBCol>
            <div className="input-group md-form form-sm form-1 pl-0">
                <div className="input-group-prepend">
                <span className="input-group-text green lighten-3" id="basic-text1">
                    <MDBIcon className="text-white" icon="search" />
                </span>
                </div>

                    <input className="form-control my-0 py-1" 
                        ref={input => this.search = input}
                        onChange={this.handleInputChange}
                        type="text" 
                        placeholder="Flurnamen suchen..." 
                        aria-label="Search" />
            </div>
            <p>{this.state.query}</p>
        </MDBCol>

        {this.state.results.map((item, index) => <ResultList key={index} {...item} />)}
      </div>
    );
  }



}

const ResultList=(props)=>(
    <p><strong>ID: </strong> {props.link}</p>
);
export default Search;
