import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import axios from "axios";


function SearchAx() {
  const [bestande, setBestande] = useState([]);
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState("");
  const [filteredBestand, setFilteredBestand] = useState([]);

  useEffect(() => {
    setLoading(true);
    //=================================================
    axios
      .get("https://restcountries.eu/rest/v2/all")
      .then((res) => {
        setBestande(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.log(err);
      });
    //==================================================
    }, 
    []);

  useEffect(() => {
    setFilteredBestand(
      bestande.filter((bestand) =>
        bestande.name.toLowerCase().includes(search.toLowerCase())
      )
    );
  }, [search, bestande]);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="App">

      <input
        type="text"
        placeholder="Search Countries"
        onChange={(e) => setSearch(e.target.value)}
      />
      {filteredBestand.map((bestand, idx) => (
        <BestandDetail key={idx} {...bestand} />
      ))}
    </div>
  );
}

const BestandDetail = (props) => {
  const { name, flag } = props;

  return (
    <>
      <p>
        <img src={flag} alt={name} style={{ width: "20px", height: "20px" }} />
      </p>
      <p>{name}</p>
    </>
  );
};

export default SearchAx;
